mapper.select:
SELECT n_no, n_title, n_write, n_date
FROM notice
WHERE n_no = #{n_no}

mapper.insert:
INSERT INTO notice (
    n_no, n_title, n_write, n_date
) VALUES (
    seq_notice.NEXTVAL,
    #{n_title}, #{n_write}, SYSDATE
)

mapper.update:
UPDATE notice
SET
    n_title = #{n_title, jdbcType=VARCHAR},
    n_write = #{n_write, jdbcType=VARCHAR}
WHERE n_no = #{n_no, jdbcType=INTEGER}
